Database.addText("nl.candyBoxRequestStatusBarUnlockedCfg", "Vraag nog eens (5 snoepjes)");
Database.addText("nl.candyBoxRequestStatusBarUnlockedSave", "Vraag nog een keer aan (5 snoepjes)");
